import React, { useState } from "react";
import styles from "./QuizStep2.module.css";

export default function QuizStep2({ onContinue }) {
  const [username, setUsername] = useState("");

  return (
    <div className={styles.container}>
      <div className={styles.instagramLogo}>
        <svg width="48" height="48" viewBox="0 0 48 48">
          <radialGradient id="ig" cx="50%" cy="50%" r="80%">
            <stop offset="0%" stopColor="#ffd600" />
            <stop offset="40%" stopColor="#ff2c56" />
            <stop offset="100%" stopColor="#6c47ff" />
          </radialGradient>
          <rect width="48" height="48" rx="14" fill="url(#ig)" />
          <circle cx="24" cy="24" r="11" fill="#fff"/>
        </svg>
      </div>
      <h2 className={styles.title}>Enter Your Instagram Handle To Start</h2>
      <p className={styles.subtitle}>
        Data is safe with us. We don’t save your personal information or passwords
      </p>

      <input
        className={styles.input}
        placeholder="@Username"
        value={username}
        onChange={e => setUsername(e.target.value)}
        autoFocus
        spellCheck="false"
      />

      <button
        className={username.length > 0 ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={!username}
        onClick={() => onContinue(username)}
      >
        Continue
      </button>
    </div>
  );
}
