import React, { useEffect, useState } from "react";
import styles from "./QuizStep12.module.css";
import InstagramEmoji from "../components/InstagramEmoji";

export default function QuizStep12({ username, onContinue }) {
  const [progress, setProgress] = useState(0);
  const [userAvatar, setUserAvatar] = useState(null);

  // Простейший способ получения аватарки Instagram
  const fetchInstagramAvatar = async (username) => {
    const cleanUsername = username.replace('@', '');
    
    try {
      // Метод 1: Через Picuki (работает без CORS)
      const picukiUrl = `https://www.picuki.com/profile/${cleanUsername}`;
      
      // Метод 2: Используем сервис для получения аватарки
      const avatarUrl = `https://instadp.com/fullsize/${cleanUsername}`;
      
      // Метод 3: Fallback - UI Avatars с первой буквой
      const firstLetter = cleanUsername.charAt(0).toUpperCase();
      const fallbackUrl = `https://ui-avatars.com/api/?name=${firstLetter}&size=160&background=ff3b82&color=fff&bold=true&format=png`;
      
      // Проверяем доступность изображения
      const img = new Image();
      img.onload = () => setUserAvatar(avatarUrl);
      img.onerror = () => setUserAvatar(fallbackUrl);
      img.src = avatarUrl;
      
    } catch (error) {
      // Fallback к дефолтной аватарке
      const firstLetter = cleanUsername.charAt(0).toUpperCase();
      setUserAvatar(`https://ui-avatars.com/api/?name=${firstLetter}&size=160&background=ff3b82&color=fff&bold=true&format=png`);
    }
  };

  useEffect(() => {
    if (username) {
      fetchInstagramAvatar(username);
    }
  }, [username]);

  useEffect(() => {
    // Event tracking
    if (window.trackEvent) {
      window.trackEvent('analysis_started', {
        username: username?.replace(/./g, '*'),
        timestamp: Date.now()
      });
    }

    // Анимация прогресс-бара
    if (progress < 100) {
      const timer = setTimeout(() => {
        setProgress(prev => Math.min(prev + 8, 100));
      }, 250);
      return () => clearTimeout(timer);
    } else {
      // Event tracking для завершения анализа
      if (window.trackEvent) {
        window.trackEvent('analysis_completed', {
          username: username?.replace(/./g, '*'),
          timestamp: Date.now()
        });
      }
      setTimeout(() => onContinue(), 1000);
    }
  }, [progress, onContinue, username]);

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <div className={styles.animationArea}>
          {/* Летающие эмодзи */}
          <div className={styles.floatingEmoji} style={{"--delay": "0s"}}>
            <InstagramEmoji emoji="❌" size={20} />
          </div>
          <div className={styles.floatingEmoji} style={{"--delay": "0.5s"}}>
            <InstagramEmoji emoji="⭐" size={20} />
          </div>
          <div className={styles.floatingEmoji} style={{"--delay": "1s"}}>
            <InstagramEmoji emoji="🔥" size={20} />
          </div>
          <div className={styles.floatingEmoji} style={{"--delay": "1.5s"}}>
            <InstagramEmoji emoji="👥" size={20} />
          </div>
          <div className={styles.floatingEmoji} style={{"--delay": "2s"}}>
            <InstagramEmoji emoji="📊" size={20} />
          </div>
          <div className={styles.floatingEmoji} style={{"--delay": "2.5s"}}>
            <InstagramEmoji emoji="💎" size={20} />
          </div>
          
          {/* Центральная аватарка */}
          <div className={styles.avatarWrapper}>
            {userAvatar ? (
              <img 
                src={userAvatar} 
                alt={`${username} avatar`} 
                className={styles.avatar}
                onError={(e) => {
                  const firstLetter = username?.replace('@', '').charAt(0).toUpperCase() || 'U';
                  e.target.src = `https://ui-avatars.com/api/?name=${firstLetter}&size=160&background=ff3b82&color=fff&bold=true&format=png`;
                }}
              />
            ) : (
              <div className={styles.avatarPlaceholder}>
                {username?.replace('@', '').charAt(0).toUpperCase() || 'U'}
              </div>
            )}
          </div>
        </div>

        <h2 className={styles.title}>
          Analyzing Your Instagram<br />
          Followers...
        </h2>
      </div>

      <div className={styles.progressSection}>
        <div className={styles.progressBar}>
          <div 
            className={styles.progressFill} 
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>
    </div>
  );
}
