import React, { useState } from "react";
import styles from "./QuizStep9.module.css";
// –î–ª—è —Å–æ–±—ã—Ç–∏–π
// import mixpanel from 'mixpanel-browser';

const options = [
  { emoji: "üîï", label: "Less noise and fake followers" },
  { emoji: "üìà", label: "Higher reach and engagement" },
  { emoji: "üòç", label: "More real and active followers" },
  { emoji: "üëå", label: "Better profile appearance" },
  { emoji: "‚ùì", label: "Other" }
];

export default function QuizStep9({ onContinue }) {
  const [selected, setSelected] = useState([]);

  const toggleOption = idx => {
    if (selected.includes(idx)) {
      setSelected(selected.filter(i => i !== idx));
    } else {
      setSelected([...selected, idx]);
    }
    // mixpanel.track('quiz_option_selected', { step_index: 9, option: options[idx].label });
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>What Improvements Do You Notice After Cleaning?</h2>
      <p className={styles.subtitle}>Select one or more options</p>
      <div className={styles.options}>
        {options.map((opt, idx) => (
          <div
            key={opt.label}
            className={`${styles.option} ${selected.includes(idx) ? styles.selected : ""}`}
            onClick={() => toggleOption(idx)}
          >
            <span className={styles.emoji}>{opt.emoji}</span>
            <span className={styles.label}>{opt.label}</span>
            {selected.includes(idx) && <span className={styles.check}>‚úîÔ∏è</span>}
          </div>
        ))}
      </div>
      <button
        className={selected.length > 0 ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={selected.length === 0}
        onClick={() => {
          // mixpanel.track('quiz_step_completed', { step_index: 9, value: selected.map(i => options[i].label) });
          onContinue(selected);
        }}
      >
        Continue
      </button>
    </div>
  );
}
