import React, { useState } from "react";
import styles from "./QuizStep11.module.css";
// import mixpanel from 'mixpanel-browser';

const options = [
  { emoji: "🚀", label: "Yes, regularly" },
  { emoji: "🔄", label: "Sometimes" },
  { emoji: "❌", label: "No, never" },
  { emoji: "⏳", label: "Planning to start soon" }
];

export default function QuizStep11({ onContinue }) {
  const [selected, setSelected] = useState(null);

  const handleSelect = idx => {
    setSelected(idx);
    // mixpanel.track('quiz_option_selected', { step_index: 11, option: options[idx].label });
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Do You Use Promotion Or Auto-Following Tools?</h2>
      <p className={styles.subtitle}>Select one option</p>
      <div className={styles.options}>
        {options.map((opt, idx) => (
          <div
            key={opt.label}
            className={`${styles.option} ${selected === idx ? styles.selected : ""}`}
            onClick={() => handleSelect(idx)}
          >
            <span className={styles.emoji}>{opt.emoji}</span>
            <span className={styles.label}>{opt.label}</span>
          </div>
        ))}
      </div>
      <button
        className={selected !== null ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={selected === null}
        onClick={() => {
          // mixpanel.track('quiz_step_completed', { step_index: 11, value: options[selected]?.label });
          onContinue(selected);
        }}
      >
        Continue
      </button>
    </div>
  );
}
