import React, { useEffect, useState } from "react";
import styles from "./QuizStep12.module.css";

export default function QuizStep12({ onContinue }) {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    // Анимация прогресс-бара
    if (progress < 100) {
      const timer = setTimeout(() => {
        setProgress(progress + 12);
      }, 330);
      return () => clearTimeout(timer);
    } else {
      setTimeout(() => onContinue(), 700); // небольшая пауза после полного прогресса
    }
  }, [progress, onContinue]);

  return (
    <div className={styles.container}>
      <div className={styles.starWrap}>
        <span className={styles.closeEmoji}>❌</span>
        <span className={styles.avatar}><img src="https://randomuser.me/api/portraits/men/11.jpg" alt="avatar" /></span>
        <span className={styles.starEmoji}>⭐️</span>
      </div>
      <h2 className={styles.title}>Analyzing Your Instagram Followers...</h2>
      <div className={styles.progressBar}>
        <div className={styles.progress} style={{width: progress + "%"}}></div>
      </div>
    </div>
  );
}
