import React, { useState, useEffect } from "react";
import styles from "./QuizStep2.module.css";
import instagramIcon from "../assets/instagram.png";

export default function QuizStep2({ onContinue }) {
  const [username, setUsername] = useState("");

  useEffect(() => {
    // Track step start time for analytics
    window.stepStartTime = Date.now();
  }, []);

  const handleUsernameChange = (e) => {
    const value = e.target.value;
    setUsername(value);
    
    // Track username entered event
    if (value.length > 2 && window.trackEvent) {
      window.trackEvent('quiz_username_entered', {
        step_index: 1,
        username: value.replace(/./g, '*') // masked for privacy
      });
    }
  };

  const handleContinue = () => {
    if (username.length > 0) {
      // Event tracking
      if (window.trackEvent) {
        window.trackEvent('quiz_step_completed', {
          step_index: 1,
          step_id: 'username_input',
          value: username.replace(/./g, '*'), // masked
          ms_spent: Date.now() - (window.stepStartTime || Date.now())
        });
      }
      onContinue(username);
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <div className={styles.iconWrapper}>
          <img 
            src={instagramIcon} 
            alt="Instagram" 
            className={styles.instagramIcon}
          />
        </div>
        
        <h2 className={styles.title}>Enter Your Instagram Handle To Start</h2>
        
        <div className={styles.subtitle}>
          <span className={styles.lockIcon}>ðŸ”’</span>
          <span>Your data is safe with us. We don't save your personal information or passwords</span>
        </div>

        <div className={styles.inputWrapper}>
          <input
            className={styles.input}
            placeholder="@Username"
            value={username}
            onChange={handleUsernameChange}
            autoFocus
            spellCheck="false"
            autoComplete="off"
            autoCapitalize="none"
          />
        </div>

        <button
          className={username.length > 0 ? styles.ctaEnabled : styles.ctaDisabled}
          disabled={!username}
          onClick={handleContinue}
        >
          Continue
        </button>
      </div>
      
      <div className={styles.progressBar}>
        <div className={styles.progressFill}></div>
      </div>
    </div>
  );
}
