import React, { useEffect } from "react";
import styles from "./Onboarding.module.css";
import storiesBlock from "../assets/stories-block.svg";
import { trackQuizViewed, trackQuizStarted } from "../mixpanel";

export default function Onboarding({ onStart }) {
  useEffect(() => {
    trackQuizViewed();
  }, []);

  const handleStart = () => {
    trackQuizStarted(0);
    onStart();
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h2 className={styles.title}>
          Discover The Truth About Your Instagram Followers — Is Your Audience Really Engaged?
        </h2>
        <p className={styles.subtitle}>
          Unlock your follower secrets! Find ghost followers, bots, and inactive accounts dragging your engagement down
        </p>
      </header>

      {/* Просто картинка, без обертывающих дивов и без popover */}
      <img
        src={storiesBlock}
        alt="Stories Block"
        className={styles.storiesBlock}
      />

      <div className={styles.steps}>
        You’re <b>0</b> steps away from a cleaner, healthier profile
      </div>

      <button className={styles.cta} onClick={handleStart}>
        Start the quiz
      </button>
    </div>
  );
}
