import React, { useState } from "react";
import styles from "./QuizStep6.module.css";

const options = [
  { emoji: "üîî", label: "Yes, please remind me" },
  { emoji: "üõ†Ô∏è", label: "No, I‚Äôll do it myself" },
  { emoji: "‚è≥", label: "Maybe later" }
];

export default function QuizStep6({ onContinue }) {
  const [selected, setSelected] = useState(null);

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Would You Like Us To Remind You Regularly To Clean Your Followers?</h2>
      <p className={styles.subtitle}>Select one option</p>
      <div className={styles.options}>
        {options.map((opt, idx) => (
          <div
            key={opt.label}
            className={`${styles.option} ${selected === idx ? styles.selected : ""}`}
            onClick={() => setSelected(idx)}
          >
            <span className={styles.emoji}>{opt.emoji}</span>
            <span className={styles.label}>{opt.label}</span>
          </div>
        ))}
      </div>
      <button
        className={selected !== null ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={selected === null}
        onClick={() => onContinue(selected)}
      >
        Continue
      </button>
    </div>
  );
}
