import React, { useEffect } from "react";
import styles from "./Onboarding.module.css";
import storiesBlock from "../assets/stories-block.png";
import mixpanel from "../mixpanel";

export default function Onboarding({ onStart }) {

  // Отправляем событие показа квиза сразу после загрузки компонента
  useEffect(() => {
    mixpanel.track("quiz_viewed", {
      referrer: document.referrer,
      device_type: navigator.userAgent,
    });
  }, []);

  // Обработчик клика по кнопке старта
  const handleStart = () => {
    mixpanel.track("quiz_started", {
      step_index: 0,
    });
    onStart();
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <h2 className={styles.title}>
          Discover The Truth About Your Instagram Followers — Is Your Audience Really Engaged?
        </h2>
        <p className={styles.subtitle}>
          Unlock your follower secrets! Find ghost followers, bots, and inactive accounts dragging your engagement down
        </p>
      </header>

      <div className={styles.storiesWrap}>
        {/* Popover */}
        <div className={styles.popover}>
          Who’s Really Following You?
          <span className={styles.triangle}></span>
        </div>
        {/* PNG stories-block (истории, аватары) */}
        <div className={styles.storiesBlock}>
          <img src={storiesBlock} width={351} height={158} alt="stories" />
        </div>
      </div>

      <div className={styles.steps}>
        You’re <b>0</b> steps away from a cleaner, healthier profile
      </div>
      <button className={styles.cta} onClick={handleStart}>Start the quiz</button>
    </div>
  );
}
