import React, { useEffect, useState } from "react";
import styles from "./Paywall.module.css";
// import mixpanel from 'mixpanel-browser';

export default function Paywall({ onPurchase }) {
  const [timer, setTimer] = useState(179); // 2:59 минуты

  useEffect(() => {
    if (timer > 0) {
      const id = setTimeout(() => setTimer(timer - 1), 1000);
      return () => clearTimeout(id);
    }
  }, [timer]);

  const timerDisplay = `${String(Math.floor(timer / 60)).padStart(2, "0")}:${String(timer % 60).padStart(2, "0")}`;

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <span className={styles.icon}>🚀</span>
        <span className={styles.icon}>🦸</span>
      </div>
      <h2 className={styles.title}>
        Unlock Real Growth — Instantly See Who Isn’t Following You!
      </h2>
      <ul className={styles.bullets}>
        <li>
          Find Ghost Followers, Bots, and Inactive Users Dragging Your Engagement Down
        </li>
        <li>
          Fast, Easy Cleanup to Boost Real Reach
        </li>
        <li>
          Smart AI Suggestions On Who To Keep Or Remove
        </li>
        <li>
          Full Privacy — No Instagram Login Needed
        </li>
      </ul>
      <div className={styles.limitedOffer}>
        <span className={styles.timer}>{timerDisplay}</span>
        <span className={styles.dealText}>Limited time: 80% off on yearly access</span>
        <span className={styles.sale}>Save 80%</span>
      </div>
      <div className={styles.prices}>
        <div className={styles.priceMain}>
          <span className={styles.check}>✔️</span>
          <span>
            <b>Yearly Plan Only $0.38/week*</b>
            <div className={styles.priceSub}>Billed $19.99 annually</div>
          </span>
          <span className={styles.saleHighlight}>Save 80%</span>
        </div>
        <div className={styles.priceAlt}>
          Weekly plan also available<br/>$4.99/week
        </div>
      </div>
      <div className={styles.trustRow}>
        <span>⭐ 4.7 App Store (7,800+ Reviews)</span>
        <span>Trusted By Over <b>120k+</b> Users Worldwide</span>
      </div>
      <div className={styles.trustRow2}>
        <span>🔒 Privacy</span>
        <span>30-day Money-Back Guarantee</span>
      </div>
      <button className={styles.cta} onClick={() => {
        // mixpanel.track('paywall_cta_click', { offer_type: 'yearly', price: 19.99 });
        onPurchase();
      }}>
        Clean My Followers — Let's Go!
      </button>
    </div>
  );
}
