import React, { useState, useEffect } from "react";
import styles from "./QuizStep2.module.css";

export default function QuizStep2({ onContinue }) {
  const [username, setUsername] = useState("");

  useEffect(() => {
    window.stepStartTime = Date.now();
  }, []);

  const handleUsernameChange = (e) => {
    let value = e.target.value;
    // –£–±–∏—Ä–∞–µ–º @ –µ—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –µ–≥–æ –≤–≤–µ–ª
    if (value.startsWith('@')) {
      value = value.slice(1);
    }
    setUsername(value);
    
    // –î–ª—è –∞–Ω–∞–ª–∏—Ç–∏–∫–∏ –¥–æ–±–∞–≤–ª—è–µ–º @ –∫–∞–∫ –º–∞—Å–∫—É
    if (value.length > 2 && window.trackEvent) {
      window.trackEvent('quiz_username_entered', {
        step_index: 1,
        username: `@${value}`.replace(/./g, '*') // –ú–∞—Å–∫–∏—Ä—É–µ–º —Å @
      });
    }
  };

  const handleContinue = () => {
    if (username.length > 0) {
      if (window.trackEvent) {
        window.trackEvent('quiz_step_completed', {
          step_index: 1,
          step_id: 'username_input',
          value: `@${username}`.replace(/./g, '*'), // –ú–∞—Å–∫–∏—Ä—É–µ–º —Å @
          ms_spent: Date.now() - (window.stepStartTime || Date.now())
        });
      }
      onContinue(`@${username}`); // –ü–µ—Ä–µ–¥–∞–µ–º —Å @
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <div className={styles.instagramLogo}>
          <svg width="80" height="80" viewBox="0 0 80 80">
            <defs>
              <radialGradient id="ig" cx="50%" cy="50%" r="70%">
                <stop offset="0%" stopColor="#ffd600" />
                <stop offset="40%" stopColor="#ff2c56" />
                <stop offset="100%" stopColor="#6c47ff" />
              </radialGradient>
            </defs>
            <rect width="80" height="80" rx="18" fill="url(#ig)" />
            {/* –ò–∫–æ–Ω–∫–∞ –∫–∞–º–µ—Ä—ã –º–µ–Ω—å—à–µ —Ñ–æ–Ω–∞ */}
            <circle cx="40" cy="40" r="12" fill="none" stroke="#fff" strokeWidth="2.5"/>
            <circle cx="40" cy="40" r="4" fill="#fff"/>
            <circle cx="54" cy="26" r="3" fill="#fff"/>
          </svg>
        </div>
        
        <h2 className={styles.title}>
          Enter Your Instagram<br />Handle To Start
        </h2>
        
        <div className={styles.subtitle}>
          <span className={styles.lockIcon}>üîí</span>
          <span>Your data is safe with us. We don't save your personal information or passwords</span>
        </div>

        <div className={styles.inputWrapper}>
          <span className={styles.atSymbol}>@</span>
          <input
            className={styles.input}
            placeholder="Username"
            value={username}
            onChange={handleUsernameChange}
            autoFocus
            spellCheck="false"
            autoComplete="off"
            autoCapitalize="none"
          />
        </div>
      </div>
      
      <div className={styles.progressBar}>
        <div className={styles.progressFill}></div>
      </div>
    </div>
  );
}
