import React, { useState } from "react";
import Onboarding from "./screens/Onboarding";
import QuizStep1 from "./screens/QuizStep1";
import QuizStep2 from "./screens/QuizStep2";
import QuizStep3 from "./screens/QuizStep3";
import QuizStep4 from "./screens/QuizStep4";
import QuizResult from "./screens/QuizResult";
import QuizStep5 from "./screens/QuizStep5";
import QuizStep6 from "./screens/QuizStep6";
import QuizStep7 from "./screens/QuizStep7";
import QuizStep8 from "./screens/QuizStep8";
import QuizStep9 from "./screens/QuizStep9";
import QuizStep10 from "./screens/QuizStep10";
import QuizStep11 from "./screens/QuizStep11";
import QuizStep12 from "./screens/QuizStep12";
import QuizFinalReport from "./screens/QuizFinalReport";
import Paywall from "./screens/Paywall";
import Success from "./screens/Success";

function App() {
  const [step, setStep] = useState(0);

  if (step === 0) return <Onboarding onStart={() => setStep(1)} />;
  if (step === 1) return <QuizStep1 onContinue={() => setStep(2)} />;
  if (step === 2) return <QuizStep2 onContinue={() => setStep(3)} />;
  if (step === 3) return <QuizStep3 onContinue={() => setStep(4)} />;
  if (step === 4) return <QuizStep4 onContinue={() => setStep(5)} />;
  if (step === 5) return <QuizResult onShowReport={() => setStep(6)} />;
  if (step === 6) return <QuizStep5 onContinue={() => setStep(7)} />;
  if (step === 7) return <QuizStep6 onContinue={() => setStep(8)} />;
  if (step === 8) return <QuizStep7 onContinue={() => setStep(9)} />;
  if (step === 9) return <QuizStep8 onContinue={() => setStep(10)} />;
  if (step === 10) return <QuizStep9 onContinue={() => setStep(11)} />;
  if (step === 11) return <QuizStep10 onContinue={() => setStep(12)} />;
  if (step === 12) return <QuizStep11 onContinue={() => setStep(13)} />;
  if (step === 13) return <QuizStep12 onContinue={() => setStep(14)} />;
  if (step === 14) return <QuizFinalReport onShowPaywall={() => setStep(15)} />;
  if (step === 15) return <Paywall onPurchase={() => setStep(16)} />;
  if (step === 16) return <Success />;
  return null;
}
export default App;
