import React, { useEffect, useState } from "react";
import styles from "./QuizStep12.module.css";
import InstagramEmoji from "../components/InstagramEmoji";

export default function QuizStep12({ username, onContinue }) {
  const [progress, setProgress] = useState(0);
  const [userAvatar, setUserAvatar] = useState(null);

  // Функция для получения аватарки пользователя Instagram
  const fetchInstagramAvatar = async (username) => {
    try {
      // Метод 1: Через публичный эндпоинт Instagram (может не работать из-за CORS)
      // const response = await fetch(`https://www.instagram.com/${username.replace('@', '')}/?__a=1`);
      
      // Метод 2: Через прокси или ваш бэкенд
      // const response = await fetch(`/api/instagram-avatar?username=${username.replace('@', '')}`);
      
      // Метод 3: Временно используем placeholder с первой буквой username
      const firstLetter = username.replace('@', '').charAt(0).toUpperCase();
      const placeholderUrl = `https://ui-avatars.com/api/?name=${firstLetter}&size=112&background=ff3b82&color=fff&bold=true`;
      
      setUserAvatar(placeholderUrl);
      
      // Если у вас есть API для получения аватарок:
      // const data = await response.json();
      // setUserAvatar(data.profile_pic_url || placeholderUrl);
      
    } catch (error) {
      console.error('Error fetching Instagram avatar:', error);
      // Fallback к дефолтной аватарке
      const firstLetter = username.replace('@', '').charAt(0).toUpperCase();
      setUserAvatar(`https://ui-avatars.com/api/?name=${firstLetter}&size=112&background=ff3b82&color=fff&bold=true`);
    }
  };

  useEffect(() => {
    if (username) {
      fetchInstagramAvatar(username);
    }
  }, [username]);

  useEffect(() => {
    // Event tracking
    if (window.trackEvent) {
      window.trackEvent('analysis_started', {
        username: username?.replace(/./g, '*'),
        timestamp: Date.now()
      });
    }

    // Анимация прогресс-бара
    if (progress < 100) {
      const timer = setTimeout(() => {
        setProgress(prev => Math.min(prev + 12, 100));
      }, 330);
      return () => clearTimeout(timer);
    } else {
      // Event tracking для завершения анализа
      if (window.trackEvent) {
        window.trackEvent('analysis_completed', {
          username: username?.replace(/./g, '*'),
          timestamp: Date.now()
        });
      }
      setTimeout(() => onContinue(), 700);
    }
  }, [progress, onContinue, username]);

  return (
    <div className={styles.container}>
      <div className={styles.content}>
        <div className={styles.iconGroup}>
          <div className={styles.iconWrapper}>
            <InstagramEmoji emoji="❌" size={24} />
          </div>
          
          <div className={styles.avatarWrapper}>
            {userAvatar ? (
              <img 
                src={userAvatar} 
                alt={`${username} avatar`} 
                className={styles.avatar}
                onError={(e) => {
                  // Fallback если изображение не загрузилось
                  const firstLetter = username?.replace('@', '').charAt(0).toUpperCase() || 'U';
                  e.target.src = `https://ui-avatars.com/api/?name=${firstLetter}&size=112&background=ff3b82&color=fff&bold=true`;
                }}
              />
            ) : (
              <div className={styles.avatarPlaceholder}>
                {username?.replace('@', '').charAt(0).toUpperCase() || 'U'}
              </div>
            )}
          </div>
          
          <div className={styles.iconWrapper}>
            <InstagramEmoji emoji="⭐" size={24} />
          </div>
        </div>

        <h2 className={styles.title}>
          Analyzing Your Instagram<br />
          Followers...
        </h2>
      </div>

      <div className={styles.progressSection}>
        <div className={styles.progressBar}>
          <div 
            className={styles.progressFill} 
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>
    </div>
  );
}
