import React, { useState } from "react";
import styles from "./QuizStep7.module.css";

const options = [
  { emoji: "üìÖ", label: "Monthly" },
  { emoji: "‚è≥", label: "Every 3-6 months" },
  { emoji: "üìÜ", label: "Once a year" },
  { emoji: "‚ö†Ô∏è", label: "Only when I notice a problem" }
];

export default function QuizStep7({ onContinue }) {
  const [selected, setSelected] = useState(null);

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>How often do you think cleaning followers should happen?</h2>
      <p className={styles.subtitle}>Select one option</p>
      <div className={styles.options}>
        {options.map((opt, idx) => (
          <div
            key={opt.label}
            className={`${styles.option} ${selected === idx ? styles.selected : ""}`}
            onClick={() => setSelected(idx)}
          >
            <span className={styles.emoji}>{opt.emoji}</span>
            <span className={styles.label}>{opt.label}</span>
          </div>
        ))}
      </div>
      <button
        className={selected !== null ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={selected === null}
        onClick={() => onContinue(selected)}
      >
        Continue
      </button>
    </div>
  );
}
