import React, { useEffect } from "react";
import styles from "./Onboarding.module.css";
import storiesBlock from "../assets/Stories.png";
import { trackQuizViewed, trackQuizStarted } from "../mixpanel";

export default function Onboarding({ onStart }) {
  useEffect(() => { trackQuizViewed(); }, []);
  const handleStart = () => { trackQuizStarted(0); onStart(); };

  return (
    <div className={styles.wrapper}>
      <header className={styles.header}>
        <h2 className={styles.title}>
          Discover The Truth About Your Instagram Followers ‚Äî Is Your Audience Really Engaged?
        </h2>
        <p className={styles.subtitle}>
          Unlock your follower secrets! Find ghost followers, bots, and inactive accounts dragging your engagement down
        </p>
      </header>

      <div className={styles.storiesSection}>
        {/* –°—Ç–∏–ª–∏–∑–æ–≤–∞–Ω–Ω—ã–µ –∏–∫–æ–Ω–∫–∏ –≤–º–µ—Å—Ç–æ —ç–º–æ–¥–∑–∏ */}
        <div className={styles.leftBubble}>
          <div className={styles.rocketIcon}>üöÄ</div>
        </div>
        <div className={styles.rightBubble}>
          <div className={styles.starIcon}>‚≠ê</div>
        </div>
        
        {/* –ù–∞–∫–ª–æ–Ω–Ω–∞—è –Ω–∞–¥–ø–∏—Å—å –±–ª–∏–∂–µ –∫ –∏—Å—Ç–æ—Ä–∏—è–º */}
        <div className={styles.speechBubble}>
          Who's Really Following You?
          <span className={styles.bubbleArrow}></span>
        </div>
        
        <img src={storiesBlock} className={styles.storiesBg} alt="Stories" draggable={false} />
      </div>

      <div className={styles.steps}>
        You're <b>0</b> steps away from a cleaner,<br/>healthier profile
      </div>

      <button className={styles.cta} onClick={handleStart}>
        Start the quiz
      </button>
    </div>
  );
}
