import React, { useState } from "react";
import styles from "./QuizStep1.module.css";

const options = [
  "Ghost followers silently killing my engagement",
  "People who don’t follow me back",
  "Fake accounts and bots cluttering my list",
  "Low activity and weak interaction",
  "Let you find all issues for me!"
];

export default function QuizStep1({ onContinue }) {
  const [selected, setSelected] = useState(null);

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Ready To Discover Who’s Really Following You?</h2>
      <p className={styles.subtitle}>
        What concerns you most: ghost followers, non-mutuals, bots, or low engagement?
      </p>

      <div className={styles.options}>
        {options.map((opt, idx) => (
          <div
            key={opt}
            className={`${styles.option} ${
              selected === idx ? styles.selected : ""
            }`}
            onClick={() => setSelected(idx)}
          >
            {opt}
          </div>
        ))}
      </div>

      <button
        className={selected !== null ? styles.ctaEnabled : styles.ctaDisabled}
        disabled={selected === null}
        onClick={() => onContinue(selected)}
      >
        Continue
      </button>
    </div>
  );
}
